from datetime import datetime, timedelta

from airflow import DAG
from airflow.operators.bash import BashOperator

default_args = {
    'owner': 'dayookubanjo',
    'retries': 5,
    'retry_delay': timedelta(minutes=5)
}

with DAG(
    dag_id='dag_with_cron_expression_v04',
    default_args=default_args,
    description='This is our first dag with cron expression',
    start_date=datetime(2022, 10, 17),
    # schedule_interval='@daily'
    # schedule_interval='0 0 * * *' # same as @daily
    # schedule_interval='0 3 * * Tue' # tuesdays at 3am
    # schedule_interval='0 3 * * Tue,Fri' # tuesdays and fridays at 3am
    schedule_interval='0 3 * * Tue-Fri' # tuesdays through fridays at 3am
) as dag:
    task1 = BashOperator(
        task_id = 'task1',
        bash_command = "echo dag with cron expression"
    )

    task1